function makeColinIeltsData () 
{
	let rrr = []
	for (var k = 1; k <= 1; k++) 
	{
		for (var i = 1; i <= 52; i++) 
		{
			const trackName = k + '.' + i

			var json = {
				track: trackName,
				en: '',
			}

			let idx = collins_cd12.findIndex(ele => ele && ele.track == trackName);
			if (idx >= 0)
			{
				var findObj = collins_cd12[idx]
				json.en = findObj.en
				json.title = findObj.title
				json.vi = findObj.vi || ''
				json.voca = findObj.voca || ''
				json.note = findObj.note || ''
			}

			rrr.push(json)
		}
	}

	collins_cd12 = rrr;
}


makeColinIeltsData ()




